            /** IPADDR = ASN_IPADDRESS */

	   sSet++;

        break;



        case COLUMN_ALLOWSMTPPORTNO:

            /** INTEGER = ASN_INTEGER */

	    sSet++;

        break;



        case COLUMN_ALLOWSMTPSTATUS:

