/*****************************************************************************

 * name             :   allowSMTPTable_Load_Container_From_File

 * description      :   Loads the SMTPTable from file to memory context

 * input parameters :

 * output parameters:

 * return type      :   void

 * global variables :

 * calls            :   init_allowSMTPTable

 *****************************************************************************/



void allowSMTPTable_Load_Container_From_File ()

{

    FILE *fp = NULL;

    int iCnt=0;

    allowSMTPTable_context stReadCtx;

    allowSMTPTable_context *pstContext = NULL;



    /* Open the container file and load all SMTPTable rows from

       this file during start up. */



    fp = fopen (SMTP_CONTAINER_FILE, "rb");

    if (fp == (FILE *) NULL)

    {

      //printf("Load_Container_From_File",                  "CONTAINER_FILE file open failed \n");

    }//end of fp

    else

    {

	/* Loading the SMTPTable rows from file to sub-agent memory */

        while (fread (&stReadCtx, sizeof (stReadCtx), 1, fp) == 1)

        {

            pstContext = SNMP_MALLOC_TYPEDEF (allowSMTPTable_context);

            memcpy (pstContext,&stReadCtx, sizeof (allowSMTPTable_context));



            netsnmp_assert(pstContext->index.oids == NULL);

            if(snmp_clone_mem( (void*)&pstContext->index.oids, &stReadCtx.idx,

                             stReadCtx.index.len * sizeof(oid) )) {

                //printf("Load_Container_From_File",                            "snmp_clone_mem failed.\n");

		fclose(fp);

                return;

            }//end of snmp_clone_mem



            pstContext->index.len = stReadCtx.index.len;



            iCnt = CONTAINER_INSERT (cb.container, pstContext);

            if (iCnt)

            {

                //printf("Load_Container_From_File",                            "ERROR IN CONTAINER_INSERT FUNCION\n");

            }//end of iCnt

       }//end of while



        //printf("Load_Container_From_File",                    "CONTAINER SIZE=%u\n", CONTAINER_SIZE (cb.container));

        /* Close the file */

        fclose (fp);



    }//else



    return;

}//Load_Container_From_File



/*****************************************************************************

 * name             :   allowSMTPTable_Search_Index_From_File

 * description      :   Loads the allowSMTPTable from file to memory context

 * input parameters :

 * output parameters:

 * return type      :   void

 * global variables :

 * calls            :   

 *****************************************************************************/



int allowSMTPTable_Search_Index_From_File (short iCurrIndex,short *strapCount)

{

    FILE *fp = NULL;

    int iIndex=0;

    short icnt;

    short sSet=0;

    char tmp[5];

    char cSMTPPort_OID[MAX_OID_LEN] = ".1.3.6.1.4.1.3204.1.3.34.4.5.1.4.";

    char cSMTPIPAddress_OID[MAX_OID_LEN] = ".1.3.6.1.4.1.3204.1.3.34.4.5.1.3.";

    char cSMTPRowStatus_OID[MAX_OID_LEN] = ".1.3.6.1.4.1.3204.1.3.34.4.5.1.5.";

    

    allowSMTPTable_context stReadCtx;

    allowSMTPTable_context *pstContext = NULL;

        

    /* Open the container file and load all POP3Table rows from

       this file during start up. */



    fp = fopen (SMTP_CONTAINER_FILE, "rb");

    if (fp == (FILE *) NULL)

    {

      //printf("Load_Container_From_File\n",                  "CONTAINER_FILE file open failed \n");

      fclose(fp);

      return USER_FAILURE;

    }//end of fp

    else

    {

        /*Search the file for the given index*/

        while (fread (&stReadCtx, sizeof (stReadCtx), 1, fp) == 1)

        {

	    memset(tmp,'\0',5);	

            pstContext = SNMP_MALLOC_TYPEDEF (allowSMTPTable_context);

            memcpy (pstContext,&stReadCtx, sizeof(allowSMTPTable_context));

	    //printf("Index value inside context: %d \n",pstContext->allowSMTPIndex);

                

            if(pstContext->allowSMTPIndex==iCurrIndex){

		  sprintf(tmp,"%d",iCurrIndex);        

                  strcat(cSMTPPort_OID,tmp);

                  strcat(cSMTPIPAddress_OID,tmp);
                  
                  strcat(cSMTPRowStatus_OID,tmp);
                  
		  if(pstContext->allowSMTPPortNo !=0){

                     memset(stSMTPTrap_Var[0].acVariable,'\0',MAX_OID_LEN);

		     memcpy(stSMTPTrap_Var[0].acVariable,cSMTPPort_OID,strlen(cSMTPPort_OID));



		     stSMTPTrap_Var[0].cType = 'i';



		  

                     memset(stSMTPTrap_Var[0].pcValue, '\0', MAX_VAR_LEN);

                     sprintf (stSMTPTrap_Var[0].pcValue, "%ld", pstContext->allowSMTPPortNo);

                  }

		  else

		  {

	             fclose(fp);

                     return USER_FAILURE;

		  }		     



		  if(pstContext->allowSMTPIPAddress != 0){

                      memset(stSMTPTrap_Var[1].acVariable,'\0',MAX_OID_LEN);

	              memcpy(stSMTPTrap_Var[1].acVariable,cSMTPIPAddress_OID,

				      strlen(cSMTPIPAddress_OID));

                 

		      stSMTPTrap_Var[1].cType = 's';



		      //printf("Before destination IP\n");

                      memset(stSMTPTrap_Var[1].pcValue,'\0',MAX_VAR_LEN);
                      memcpy(stSMTPTrap_Var[1].pcValue,(char *)(&pstContext->allowSMTPIPAddress),sizeof(pstContext->allowSMTPIPAddress));

                      //sprintf (stSMTPTrap_Var[1].pcValue, "%ld",pstContext->allowSMTPIPAddress);

		  }

		  else

		  {

	             fclose(fp);

                     return USER_FAILURE;

		  }		     


		  if(pstContext->allowSMTPStatus==2){

                     memset(stSMTPTrap_Var[2].acVariable,'\0',MAX_OID_LEN);

		     memcpy(stSMTPTrap_Var[2].acVariable,cSMTPRowStatus_OID,strlen(cSMTPRowStatus_OID));



		     stSMTPTrap_Var[2].cType = 'i';



		  

                     memset(stSMTPTrap_Var[2].pcValue, '\0', MAX_VAR_LEN);

                     sprintf (stSMTPTrap_Var[2].pcValue, "%ld", pstContext->allowSMTPStatus);
                    *strapCount = 3;  

                  }
                  else
                   *strapCount = 2;



                 fclose(fp);

		 return USER_SUCCESS;  

             }//end of if

	     

	   }//end of while

	 fclose(fp);

	 return USER_FAILURE;

	}//end of else

	

}//end of function



