/*****************************************************************************
 * name             :   backupMasterTable_Load_Container_From_File
 * description      :   Loads the POP3Table from file to memory context
 * input parameters :
 * output parameters:
 * return type      :   void
 * global variables :
 * calls            :   init_backupMasterTable
 *****************************************************************************/

void backupMasterTable_Load_Container_From_File ()
{
    FILE *fp = NULL;
    int iCnt=0;
    backupMasterTable_context stReadCtx;
    backupMasterTable_context *pstContext = NULL;

    /* Open the container file and load all POP3Table rows from
       this file during start up. */

    fp = fopen (BM_CONTAINER_FILE, "rb");
    if (fp == (FILE *) NULL)
    {
      //printf("Load_Container_From_File",                  "CONTAINER_FILE file open failed \n");
    }//end of fp
    else
    {
	/* Loading the POP3Table rows from file to sub-agent memory */
        while (fread (&stReadCtx, sizeof (stReadCtx), 1, fp) == 1)
        {
            pstContext = SNMP_MALLOC_TYPEDEF (backupMasterTable_context);
            memcpy (pstContext,&stReadCtx, sizeof (backupMasterTable_context));

            netsnmp_assert(pstContext->index.oids == NULL);
            if(snmp_clone_mem( (void*)&pstContext->index.oids, &stReadCtx.idx,
                             stReadCtx.index.len * sizeof(oid) )) {
                //printf("Load_Container_From_File",                            "snmp_clone_mem failed.\n");
                fclose (fp);
                return;
            }//end of snmp_clone_mem

            pstContext->index.len = stReadCtx.index.len;

            iCnt = CONTAINER_INSERT (cb.container, pstContext);
            if (iCnt)
            {
                //printf("Load_Container_From_File",                            "ERROR IN CONTAINER_INSERT FUNCION\n");
            }//end of iCnt
       }//end of while

        //printf("Load_Container_From_File",                    "CONTAINER SIZE=%u\n", CONTAINER_SIZE (cb.container));
        /* Close the file */
        fclose (fp);

    }//else

    return;
}//Load_Container_From_File


