/*****************************************************************************
 * name             :   RTM_callback
 * description      :   callback function called every RTM_INTERVAL seconds
 *                      configured in configuration file. Default interval is
 *                      1 second.
 * input parameters :   unsigned int uiReg - snmp_alarm_register callback regid
 *                      void *clientarg - void pointer - unused
 * output parameters:
 * return type      :   void
 * global variables :   rtm_regid, fpath
 * calls            :   init_trapTable
 *****************************************************************************/

void
RTM_callback (unsigned int uiReg, void *clientarg)
{
    unsigned long ulVal;
    int iCnt=0;
    int iErr=0;
    char szStrOid[MAX_OID_LEN] = { 0 };
    char szTmpStr[MAX_OID_LEN] = { 0 };
    int  iTrapVarLen = 0;
    int  iSendTrap   = 0;
    stVarBindArgs aArgs[3];
    trapTable_context *pstCtx = NULL;

    /* Unregister pending alarm registerd by RTM, if any */

    if (rtm_regid != 0)
    {
        DEBUGMSGTL(("nuri/rtm", "Unregistering rtm_regid.....\n"));
        snmp_alarm_unregister(rtm_regid);
        rtm_regid = 0;
    }

    /* Get the first row of trapTable */
    pstCtx = CONTAINER_NEXT (cb.container, NULL);

#ifdef RTM_LOG_FILE
    if (!fpt)
    {
        /* remove, if surrounded by quotes. fpath is the logfile path */
        RTM_trim_quotes (fpath);

        if ((fpt = fopen (fpath, "w")) == (FILE *) NULL)
        {
            DEBUGMSGTL(("nuri/rtm",
                        "Couldn't open %s file errno=%d\n", fpath, errno));
            return;
        }
    }
#endif /* RTM_LOG_FILE */

    while (pstCtx)
    {
        if (RS_IS_ACTIVE (pstCtx->trapStatus) == 0)
        {
           DEBUGMSGTL(("nuri/rtm", "Skipping Non-Active Row: "
                       "pstCtx->trapIndex=%ld trapStatus=%ld\n",
                       pstCtx->trapIndex, pstCtx->trapStatus));

            pstCtx = CONTAINER_NEXT (cb.container, pstCtx);
            continue;
        }

        iTrapVarLen = pstCtx->trapVariable_len / sizeof (oid);

        memset (szStrOid, '\0', sizeof (szStrOid));

        for (iCnt=0; iCnt < iTrapVarLen; iCnt++)
        {
            sprintf (szTmpStr, ".%lu", pstCtx->trapVariable[iCnt]);
            strcat (szStrOid, szTmpStr);
        }

        memset (szTmpStr, '\0', sizeof (szTmpStr));

        /* If trapVariable is zero, skip the row */
        if (pstCtx->trapVariable[0] == 0)
        {
           DEBUGMSGTL(("nuri/rtm", "Skipping Row: trapVariable not set!! "
                       "pstCtx->trapIndex=%ld trapStatus=%ld\n",
                       pstCtx->trapIndex, pstCtx->trapStatus));
           pstCtx = CONTAINER_NEXT (cb.container, pstCtx);
           continue;
        }

        /* Retrieve the value the trapVariable through snmpget request */

        iErr = RTM_Fetch_Var_Val (pstCtx->trapVariable,iTrapVarLen,&ulVal);
        if (iErr)
        {
#ifdef RTM_LOG_FILE
            if (iErr == 2)
            {
                fprintf (fpt, "Couldn't fetch value of variable OID: <%s> "
                        "for trapIndex=%lu\n", szStrOid, pstCtx->trapIndex);
            }
#endif /* RTM_LOG_FILE */
            DEBUGMSGTL(("nuri/rtm",
                  "Couldn't fetch variable's value iErr=%d for trapIndex=%lu\n",
                  iErr, pstCtx->trapIndex));
            pstCtx = CONTAINER_NEXT (cb.container, pstCtx);
            continue;
        }
	// Added for sysStatistics Calculation - Raj
	updateRow(pstCtx->trapIndex,ulVal);
        sprintf(szTmpStr,"%ld",ulVal);

        DEBUGMSGTL(("nuri/rtm",
            "RTM_Fetch_Var_Val successful. return value=%s\n", szTmpStr));

        /* Validate trapVariable with trapExpression and Threshold value and
	   returns a boolean value (TRUE or FALSE) for sending a trap */

        iSendTrap = Validate_trapVariable (szTmpStr, pstCtx);

        if (iSendTrap == TRUE)
        {
            DEBUGMSGTL(("nuri/rtm",
                        "SendTrap is TRUE. Calling send_snmptrap\n"));

            /* Fill up Argument details to send in a trap message */
            /* strcpy (aArgs[0].variable, NURI_SUBAGENT_PORT_OID); */
            sprintf (aArgs[0].variable, "%s.%lu",
                                   NURI_SUBAGENT_PORT_OID, pstCtx->trapIndex);

            aArgs[0].type = 'i';
            sprintf (aArgs[0].value, "%d", giMyPort);

            sprintf (aArgs[1].variable, "%s.%lu",
                                     NURI_TRAP_POLICY_OID, pstCtx->trapIndex);
            aArgs[1].type = 'u';
            sprintf (aArgs[1].value, "%lu", pstCtx->trapPolicyID);

            sprintf (aArgs[2].variable, "%s.%lu",
                                       NURI_TRAP_MSG_OID, pstCtx->trapIndex);
            aArgs[2].type = 's';

            pstCtx->trapMessage [pstCtx->trapMessage_len] = '\0';
            memcpy (aArgs[2].value, pstCtx->trapMessage,
                                                  pstCtx->trapMessage_len+1);
            
            /* If return value is TRUE, send trap request with policyId and
               log this activity in detail in a log file */

            send_snmptrap (3, aArgs, pstCtx);

#ifdef RTM_LOG_FILE
            RTM_Log_Trap_Details (3, aArgs, pstCtx);
#endif
        }

        pstCtx = CONTAINER_NEXT (cb.container, pstCtx);
    }

    rtm_regid = snmp_alarm_register (interval, (int) NULL, RTM_callback, NULL);

}

/*****************************************************************************
 * name             :   RTM_Load_Container_From_File
 * description      :   loading trapTable rows from CONTAINER file
 *                      into memory during startup
 * input parameters :
 * output parameters:
 * return type      :   void
 * global variables :
 * calls            :   init_trapTable
 *****************************************************************************/

void
RTM_Load_Container_From_File ()
{
    FILE *fp=NULL;
    int iCnt=0;
    int iTrapVarLen = 0;
    char szStrOid[MAX_OID_LEN] = { 0 };
    char szTmpStr[MAX_OID_LEN] = { 0 };
    struct in_addr stAddress;
    trapTable_context stReadCtx;
    trapTable_context *pstCtx = NULL;

    /* Open the data file where persistent data of trapTable rows are stored */
    fp = fopen (RTM_CONTAINER_FILE, "rb");
    if (fp == (FILE *) NULL)
    {
        DEBUGMSGTL(("nuri/rtm",
                    "RTM_CONTAINER_FILE file open failed errno=%d\n", errno));
    }
    else
    {
        /* Load each record (row) from file into memory */
        while (fread (&stReadCtx, sizeof (trapTable_context), 1, fp) == 1)
        {
            iTrapVarLen = stReadCtx.trapVariable_len / sizeof (oid);
            memset (szStrOid, 0, sizeof (szStrOid));
            for (iCnt=0; iCnt < iTrapVarLen; iCnt++)
            {
                sprintf (szTmpStr, ".%lu", stReadCtx.trapVariable[iCnt]);
                strcat (szStrOid, szTmpStr);
            }
            stAddress.s_addr = stReadCtx.trapDestinationIP;
            memset (szTmpStr, 0, sizeof (szTmpStr));
            strcpy (szTmpStr, (char *) inet_ntoa (stAddress));

            DEBUGMSGTL(("nuri/rtm", "Reading from FILE: Index=%u policyId=%u "
                  "trapVariable=%s trapExpression=%ld trapValue=%ld "
                  "trapDestinationIP=%s trapPort=%ld\n", stReadCtx.trapIndex,
                  stReadCtx.trapPolicyID, szStrOid, stReadCtx.trapExpression,
                  stReadCtx.trapValue, szTmpStr, stReadCtx.trapPort));

            pstCtx = SNMP_MALLOC_TYPEDEF (trapTable_context);
            memcpy (pstCtx, &stReadCtx, sizeof (trapTable_context));

            netsnmp_assert(pstCtx->index.oids == NULL);
            if (snmp_clone_mem( (void*)&pstCtx->index.oids, &stReadCtx.idx,
                                 stReadCtx.index.len * sizeof(oid) )) {
                return;
            }

            pstCtx->index.len = stReadCtx.index.len;

            /* Insert the buffer into memory */
            iCnt = CONTAINER_INSERT (cb.container, pstCtx);
            if (iCnt)
            {
                DEBUGMSGTL(("nuri/rtm", "ERROR IN CONTAINER_INSERT FUNCION\n"));
            }
	   // Added for sysStatistics Table - Raj
	   createSysStatRow(pstCtx->trapIndex,pstCtx->trapVariable,
					pstCtx->trapVariable_len);
        }

        DEBUGMSGTL(("nuri/rtm",
                    "CONTAINER SIZE=%u\n", CONTAINER_SIZE (cb.container)));
        if (fp)
            fclose (fp);
    } /* else */

    return;
}

/*****************************************************************************
 * name             :   RTM_trim
 * description      :   trims extra spaces from both the ends of input string
 * input parameters :
 * output parameters:
 * return type      :   void
 * global variables :
 * calls            :   RTM_Load_Details_From_Config_File
 *****************************************************************************/

void
RTM_trim (char *str)
{
  int i=0;
  int j=0;

  while (isspace ((int) str[i]))
    i++;
  j = strlen (str);
  while (isspace((int) str[j-1]))
    j--;
  str[j] = '\0';
  str = &str[i];
  return;
}

/*****************************************************************************
 * name             :   RTM_trim_quotes
 * description      :   trim the quotes from both the ends of input string
 * input parameters :   char *str - input string
 * output parameters:
 * return type      :   void
 * global variables :
 * calls            :   RTM_callback
 *****************************************************************************/

void
RTM_trim_quotes (char *str)
{
    if (str[0] == '"')
        str = str+1;      

    if (str[strlen (str) - 1] == '"')
        str[strlen (str) - 1] = '\0';

    return;
}

/*****************************************************************************
 * name             :   RTM_Load_Details_From_Config_File
 * description      :   To load RTM_INTERVAL, RTM_LOGFILE_PATH and
 *                      RTM_LOGFILE_SIZE parameters from the configuration file.
 * input parameters :
 * output parameters:
 * return type      :   void
 * global variables :   fpath, interval, fsize
 * calls            :   init_trapTable
 *****************************************************************************/

void
RTM_Load_Details_From_Config_File ()
{
    FILE *cfp;
    char *token;
    char line[150]= { 0 };

    strcpy (fpath, RTM_DEFAULT_LOGFILE_PATH);

    /* Open the configuration file */
    cfp = fopen (RTM_CONFIG_FILE, "r");

    if (cfp != NULL)
    /* Read each line at a time and check for parameters */
    while (fgets (line, sizeof (line), cfp) != NULL)
    {
        /* remove any spaces at the beginning or at the end of line */
        RTM_trim (line);

        /* Skip comments, if any */
        if (line[0] == '#')
        {
            continue;
        }
        if (strstr (line, "RTM_INTERVAL"))
        {
            if ((token = (char *) strtok (line, "=")) != NULL)
            {
                if ((token = (char *) strtok (NULL, "\n")) != NULL)
                {
                    RTM_trim (token);
                    if (token != NULL && ((interval = atoi (token)) <= 0))
                        interval = RTM_DEFAULT_INTERVAL;
                }
            }
        }
        else if (strstr (line, "RTM_LOGFILE_PATH"))
        {
            if ((token = (char *) strtok (line, "=")) != NULL)
            {
                if ((token = (char *) strtok (NULL, "\n")) != NULL)
                {
                    RTM_trim (token);
                    strcpy (fpath, token);
                }
            }
        }
        else if (strstr (line, "RTM_MAX_LOGFILE_SIZE"))
        {
            if ((token=(char *) strtok (line, "=")) != NULL)
            {
                if ((token = (char *) strtok (NULL, "\n")) != NULL)
                {
                    RTM_trim (token);
                    if (token != NULL && ((fsize = atol (token)) <= 0))
                        fsize = RTM_DEFAULT_MAX_LOGFILE_SIZE;
                }
            }
        }
    } /* while */
    else /* (cfp == NULL) */
    {
        DEBUGMSGTL(("nuri/rtm", "Unable to open RTM_CONFIG_FILE errno=%d! "
         "Taking default configuration details\n", errno));
    }

    DEBUGMSGTL(("nuri/rtm",
                "interval=%d fpath=%s fsize=%ld\n", interval, fpath, fsize));
    if (cfp)
        fclose (cfp);

    /* Open MA.conf and get the port number of NASCenterAgent */
    cfp = fopen (RTM_MA_FILE, "r");
    if (cfp == NULL)
    {
        DEBUGMSGTL(("nuri/rtm", "Unable to open RTM_MA_FILE errno=%d! "
                    "Taking default configuration details\n", errno));
        return;
    }

    /* Read each line at a time and check for parameters */
    while (fscanf (cfp, "%s", line) > 0)
    {
        /* remove any spaces at the beginning or at the end of line */
        RTM_trim (line);

        /* Skip comments, if any */
        if (line[0] == '#')
        {
            continue;
        }
        if (!strcmp (line,"NASSA_PORT"))
        {
            fscanf (cfp, "%d", &giMyPort);
        }
    } /* while */

    if (giMyPort == 0)
        giMyPort = DEFAULT_SA_PORT;

    DEBUGMSGTL(("nuri/rtm", "SA Port from MA.conf=<%d>\n", giMyPort));

    if (cfp)
        fclose (cfp);
    return;
}

