/************************************************************
 * the *_row_copy routine
 */
static int transactionTable_row_copy(transactionTable_context * dst,
                         transactionTable_context * src)
{
    if(!dst||!src)
        return 1;
        
    /*
     * copy index, if provided
     */
    if(dst->index.oids)
        free(dst->index.oids);
    if(snmp_clone_mem( (void*)&dst->index.oids, src->index.oids,
                           src->index.len * sizeof(oid) )) {
        dst->index.oids = NULL;
        return 1;
    }
    dst->index.len = src->index.len;
    

    /*
     * copy components into the context structure
     */
    dst->transactionIndex = src->transactionIndex;

    memcpy( dst->transactionClientAddress, src->transactionClientAddress, src->transactionClientAddress_len );
    dst->transactionClientAddress_len = src->transactionClientAddress_len;

    memcpy( dst->transactionApplication, src->transactionApplication, src->transactionApplication_len );
    dst->transactionApplication_len = src->transactionApplication_len;

    dst->transactionTransportPort = src->transactionTransportPort;

    dst->transactionSampleInterval = src->transactionSampleInterval;

    memcpy( dst->transactionCreateDateTime, src->transactionCreateDateTime, src->transactionCreateDateTime_len );
    dst->transactionCreateDateTime_len = src->transactionCreateDateTime_len;

    memcpy( dst->transactionDescription, src->transactionDescription, src->transactionDescription_len );
    dst->transactionDescription_len = src->transactionDescription_len;

    memcpy( dst->transactionOwner, src->transactionOwner, src->transactionOwner_len );
    dst->transactionOwner_len = src->transactionOwner_len;

    memcpy( dst->transactionCurrentClientAddress, src->transactionCurrentClientAddress, src->transactionCurrentClientAddress_len );
    dst->transactionCurrentClientAddress_len = src->transactionCurrentClientAddress_len;

    memcpy( dst->transactionCurrentTransactionApp, src->transactionCurrentTransactionApp, src->transactionCurrentTransactionApp_len );
    dst->transactionCurrentTransactionApp_len = src->transactionCurrentTransactionApp_len;

    dst->transactionResponseTime = src->transactionResponseTime;

    dst->transactionInOctets = src->transactionInOctets;

    dst->transactionInPkts = src->transactionInPkts;

    dst->transactionOutOctets = src->transactionOutOctets;

    dst->transactionOutPkts = src->transactionOutPkts;

    dst->transactionStatus = src->transactionStatus;

    return 0;
}

