/************************************************************
 * the *_duplicate row routine
 */
transactionTable_context *
transactionTable_duplicate_row( transactionTable_context * row_ctx)
{
    transactionTable_context * dup;

    if(!row_ctx)
        return NULL;

    dup = SNMP_MALLOC_TYPEDEF(transactionTable_context);
    if(!dup)
        return NULL;
        
    if(transactionTable_row_copy(dup,row_ctx)) {
        free(dup);
        dup = NULL;
    }

    return dup;
}

