/************************************************************
 * RESERVE is used to check the syntax of all the variables
 * provided, that the values being set are sensible and consistent,
 * and to allocate any resources required for performing the SET.
 * After this stage, the expectation is that the set ought to
 * succeed, though this is not guaranteed. (In fact, with the UCD
 * agent, this is done in two passes - RESERVE1, and
 * RESERVE2, to allow for dependancies between variables).
 *
 * BEFORE calling this routine, the agent will call duplicate_row
 * to create a copy of the row (unless this is a new row; i.e.
 * row_created == 1).
 *
 * next state -> SET_RESERVE2 || SET_FREE
 */
void transactionTable_set_reserve1( netsnmp_request_group *rg )
{
    transactionTable_context *row_ctx =
            (transactionTable_context *)rg->existing_row;
    transactionTable_context *undo_ctx =
            (transactionTable_context *)rg->undo_info;
    netsnmp_variable_list *var;
    netsnmp_request_group_item *current;
    int rc;


    /*
     * TODO: loop through columns, check syntax and lengths. For
     * columns which have no dependencies, you could also move
     * the value/range checking here to attempt to catch error
     * cases as early as possible.
     */
    for( current = rg->list; current; current = current->next ) {

        var = current->ri->requestvb;
        rc = SNMP_ERR_NOERROR;

        switch(current->tri->colnum) {

        case COLUMN_TRANSACTIONCLIENTADDRESS:
            /** OCTETSTR = ASN_OCTET_STR */
            rc = netsnmp_check_vb_type_and_size(var, ASN_OCTET_STR,
                                    var->val_len);
        break;

        case COLUMN_TRANSACTIONAPPLICATION:
            /** DisplayString = ASN_OCTET_STR */
            rc = netsnmp_check_vb_type_and_size(var, ASN_OCTET_STR,
                                     var->val_len);
        break;

        case COLUMN_TRANSACTIONTRANSPORTPORT:
            /** INTEGER = ASN_INTEGER */
            rc = netsnmp_check_vb_type_and_size(var, ASN_INTEGER,
                                                sizeof(row_ctx->transactionTransportPort));
        break;

        case COLUMN_TRANSACTIONSAMPLEINTERVAL:
            /** INTEGER = ASN_INTEGER */
            rc = netsnmp_check_vb_type_and_size(var, ASN_INTEGER,
                                                sizeof(row_ctx->transactionSampleInterval));
        break;

        case COLUMN_TRANSACTIONCREATEDATETIME:
            /** DateAndTime = ASN_OCTET_STR */
            rc = netsnmp_check_vb_type_and_size(var, ASN_OCTET_STR,
                                      var->val_len);
        break;

        case COLUMN_TRANSACTIONDESCRIPTION:
            /** DisplayString = ASN_OCTET_STR */
            rc = netsnmp_check_vb_type_and_size(var, ASN_OCTET_STR,
                                      var->val_len);
        break;

        case COLUMN_TRANSACTIONOWNER:
            /** OCTETSTR = ASN_OCTET_STR */
            rc = netsnmp_check_vb_type_and_size(var, ASN_OCTET_STR,
                                       var->val_len);
        break;

        case COLUMN_TRANSACTIONCURRENTCLIENTADDRESS:
            /** OCTETSTR = ASN_OCTET_STR */
            rc = netsnmp_check_vb_type_and_size(var, ASN_OCTET_STR,
                                        var->val_len);
        break;

        case COLUMN_TRANSACTIONCURRENTTRANSACTIONAPP:
            /** DisplayString = ASN_OCTET_STR */
            rc = netsnmp_check_vb_type_and_size(var, ASN_OCTET_STR,
                                    var->val_len);
        break;

        case COLUMN_TRANSACTIONRESPONSETIME:
            /** INTEGER = ASN_INTEGER */
            rc = netsnmp_check_vb_type_and_size(var, ASN_INTEGER,
                                                sizeof(row_ctx->transactionResponseTime));
        break;

        case COLUMN_TRANSACTIONINOCTETS:
            /** COUNTER = ASN_COUNTER */
            rc = netsnmp_check_vb_type_and_size(var, ASN_COUNTER,
                                                sizeof(row_ctx->transactionInOctets));
        break;

        case COLUMN_TRANSACTIONINPKTS:
            /** COUNTER = ASN_COUNTER */
            rc = netsnmp_check_vb_type_and_size(var, ASN_COUNTER,
                                                sizeof(row_ctx->transactionInPkts));
        break;

        case COLUMN_TRANSACTIONOUTOCTETS:
            /** COUNTER = ASN_COUNTER */
            rc = netsnmp_check_vb_type_and_size(var, ASN_COUNTER,
                                                sizeof(row_ctx->transactionOutOctets));
        break;

        case COLUMN_TRANSACTIONOUTPKTS:
            /** COUNTER = ASN_COUNTER */
            rc = netsnmp_check_vb_type_and_size(var, ASN_COUNTER,
                                                sizeof(row_ctx->transactionOutPkts));
        break;

        case COLUMN_TRANSACTIONSTATUS:
            /** RowStatus = ASN_INTEGER */
            rc = netsnmp_check_vb_type_and_size(var, ASN_INTEGER,
                                                sizeof(row_ctx->transactionStatus));
        break;

        default: /** We shouldn't get here */
            rc = SNMP_ERR_GENERR;
            snmp_log(LOG_ERR, "unknown column in "
                     "transactionTable_set_reserve1\n");
        }

        if (rc)
           netsnmp_set_mode_request_error(MODE_SET_BEGIN, current->ri, rc );
        rg->status = SNMP_MAX( rg->status, current->ri->status );
    }

    /*
     * done with all the columns. Could check row related
     * requirements here.
     */
}

