/* Begin: WIP_NURI_ALL */
int Tr_InitPcap() {
	gcDevice = pcap_lookupdev(gcErrorBuff);
	if(gcDevice==NULL){
		return -1;
	}
	*gcErrorBuff = '\0';
    pcap_lookupnet(gcDevice, &gNetId, &gNetMask, gcErrorBuff);
	gPcapHandle = pcap_open_live(gcDevice,BUFSIZ,1,0,gcErrorBuff);
	if(gPcapHandle==NULL){
		return -1;
	}
	return 1;
}

void Tr_ResetCounters(transactionTable_context *ctx) {
	pthread_mutex_lock(&gContainerLock);
	ctx->transactionInOctets=0;
	ctx->transactionInPkts=0;
	ctx->transactionOutOctets=0;
	ctx->transactionOutPkts=0;
	ctx->transactionResponseTime=0;
	pthread_mutex_unlock(&gContainerLock);
}


