/************************************************************
 *
 * Initialize the appMeteringTable table by defining its contents and how it's structured
 */
void
initialize_table_appMeteringTable(void)
{
    netsnmp_table_registration_info *table_info;

    if(my_handler) {
        snmp_log(LOG_ERR, "initialize_table_appMeteringTable_handler called again\n");
        return;
    }

    memset(&cb, 0x00, sizeof(cb));

    /** create the table structure itself */
    table_info = SNMP_MALLOC_TYPEDEF(netsnmp_table_registration_info);

    /* if your table is read only, it's easiest to change the
       HANDLER_CAN_RWRITE definition below to HANDLER_CAN_RONLY */
    my_handler = netsnmp_create_handler_registration("appMeteringTable",
                                             netsnmp_table_array_helper_handler,
                                             appMeteringTable_oid,
                                             appMeteringTable_oid_len,
                                             HANDLER_CAN_RWRITE);
            
    if (!my_handler || !table_info) {
        snmp_log(LOG_ERR, "malloc failed in "
                 "initialize_table_appMeteringTable_handler\n");
        return; /** mallocs failed */
    }

    /***************************************************
     * Setting up the table's definition
     */
    /*
     * TODO: add any external indexes here.
     */

    /*
     * internal indexes
     */
        /** index: appMeteringIndex */
        netsnmp_table_helper_add_index(table_info, ASN_UNSIGNED);

    table_info->min_column = appMeteringTable_COL_MIN;
    table_info->max_column = appMeteringTable_COL_MAX;

    /***************************************************
     * registering the table with the master agent
     */
    cb.get_value = appMeteringTable_get_value;
    cb.container = netsnmp_container_find("appMeteringTable_primary:"
                                          "appMeteringTable:"
                                          "table_container");
#ifdef appMeteringTable_IDX2
    netsnmp_container_add_index(cb.container,
                                netsnmp_container_find("appMeteringTable_secondary:"
                                                       "appMeteringTable:"
                                                       "table_container"));
    cb.container->next->compare = appMeteringTable_cmp;
#endif
#ifdef appMeteringTable_SET_HANDLING
    cb.can_set = 1;
#ifdef appMeteringTable_ROW_CREATION
    cb.create_row = (UserRowMethod*)appMeteringTable_create_row;
#endif
    cb.duplicate_row = (UserRowMethod*)appMeteringTable_duplicate_row;
    cb.delete_row = (UserRowMethod*)appMeteringTable_delete_row;
    cb.row_copy = (Netsnmp_User_Row_Operation *)appMeteringTable_row_copy;

    cb.can_activate = (Netsnmp_User_Row_Action *)appMeteringTable_can_activate;
    cb.can_deactivate = (Netsnmp_User_Row_Action *)appMeteringTable_can_deactivate;
    cb.can_delete = (Netsnmp_User_Row_Action *)appMeteringTable_can_delete;

    cb.set_reserve1 = appMeteringTable_set_reserve1;
    cb.set_reserve2 = appMeteringTable_set_reserve2;
    cb.set_action = appMeteringTable_set_action;
    cb.set_commit = appMeteringTable_set_commit;
    cb.set_free = appMeteringTable_set_free;
    cb.set_undo = appMeteringTable_set_undo;
#endif
    DEBUGMSGTL(("initialize_table_appMeteringTable",
                "Registering table appMeteringTable "
                "as a table array\n"));
    netsnmp_table_container_register(my_handler, table_info, &cb,
                                     cb.container, 1);
}

