/************************************************************
 * the *_row_copy routine
 */
static int icpTable_row_copy(icpTable_context * dst,
                         icpTable_context * src)
{
    if(!dst||!src)
        return 1;
        
    /*
     * copy index, if provided
     */
    if(dst->index.oids)
        free(dst->index.oids);
    if(snmp_clone_mem( (void*)&dst->index.oids, src->index.oids,
                           src->index.len * sizeof(oid) )) {
        dst->index.oids = NULL;
        return 1;
    }
    dst->index.len = src->index.len;
    

    /*
     * copy components into the context structure
     */
    dst->icpIndex = src->icpIndex;

    memcpy( dst->icpUserId, src->icpUserId, src->icpUserId_len );
    dst->icpUserId_len = src->icpUserId_len;

    memcpy( dst->icpCommand, src->icpCommand, src->icpCommand_len );
    dst->icpCommand_len = src->icpCommand_len;

    dst->icpStatus = src->icpStatus;

    return 0;
}

