/************************************************************
 * the *_duplicate row routine
 */
icpTable_context *
icpTable_duplicate_row( icpTable_context * row_ctx)
{
    icpTable_context * dup;

    if(!row_ctx)
        return NULL;

    dup = SNMP_MALLOC_TYPEDEF(icpTable_context);
    if(!dup)
        return NULL;
        
    if(icpTable_row_copy(dup,row_ctx)) {
        free(dup);
        dup = NULL;
    }

    return dup;
}

