/************************************************************
 * Assuming that the RESERVE phases were successful, the next
 * stage is indicated by the action value ACTION. This is used
 * to actually implement the set operation. However, this must
 * either be done into temporary (persistent) storage, or the
 * previous value stored similarly, in case any of the subsequent
 * ACTION calls fail.
 *
 * In your case, changes should be made to row_ctx. A copy of
 * the original row is in undo_ctx.
 */
void icpTable_set_action( netsnmp_request_group *rg )
{
    netsnmp_variable_list *var;
    icpTable_context *row_ctx = (icpTable_context *)rg->existing_row;
    icpTable_context *undo_ctx = (icpTable_context *)rg->undo_info;
    netsnmp_request_group_item *current;

    int            row_err = 0;

    /*
     * TODO: loop through columns, copy varbind values
     * to context structure for the row.
     */
    for( current = rg->list; current; current = current->next ) {

        var = current->ri->requestvb;

        switch(current->tri->colnum) {

        case COLUMN_ICPUSERID:
            /** DisplayString = ASN_OCTET_STR */
            memcpy(row_ctx->icpUserId,var->val.string,var->val_len);
            row_ctx->icpUserId_len = var->val_len;
        break;

        case COLUMN_ICPCOMMAND:
            /** DisplayString = ASN_OCTET_STR */
            memcpy(row_ctx->icpCommand,var->val.string,var->val_len);
            row_ctx->icpCommand_len = var->val_len;
        break;

        case COLUMN_ICPSTATUS:
            /** RowStatus = ASN_INTEGER */
            row_ctx->icpStatus = *var->val.integer;
        break;

        default: /** We shouldn't get here */
            netsnmp_assert(0); /** why wasn't this caught in reserve1? */
        }
    }

    /*
     * done with all the columns. Could check row related
     * requirements here.
     */
#ifndef icpTable_CAN_MODIFY_ACTIVE_ROW
    if( undo_ctx && RS_IS_ACTIVE(undo_ctx->icpStatus) &&
        row_ctx && RS_IS_ACTIVE(row_ctx->icpStatus) ) {
            row_err = 1;
    }
#endif

    /*
     * check activation/deactivation
     */
    row_err = netsnmp_table_array_check_row_status(&cb, rg,
                                  row_ctx ? &row_ctx->icpStatus : NULL,
                                  undo_ctx ? &undo_ctx->icpStatus : NULL);
    if(row_err) {
        netsnmp_set_mode_request_error(MODE_SET_BEGIN,
                                       (netsnmp_request_info*)rg->rg_void,
                                       row_err);
        return;
    }

    if((row_ctx->icpCommand_len ==0) &&
	    (row_ctx->icpStatus ==4 ||
             row_ctx->icpStatus ==1)) {
	    row_err = 1;
    }

    if(row_err){
	    
	 netsnmp_set_mode_request_error(MODE_SET_BEGIN,
                                       (rg->list->ri),
                                       row_err);
         return;
    }
   

    /*
     * TODO: if you have dependencies on other tables, this would be
     * a good place to check those, too.
     */
}

/************************************************************
 * Only once the ACTION phase has completed successfully, can
 * the final COMMIT phase be run. This is used to complete any
 * writes that were done into temporary storage, and then release
 * any allocated resources. Note that all the code in this phase
 * should be "safe" code that cannot possibly fail (cue
 * hysterical laughter). The whole intent of the ACTION/COMMIT
 * division is that all of the fallible code should be done in
 * the ACTION phase, so that it can be backed out if necessary.
 *
 * BEFORE calling this routine, the agent will update the
 * container (inserting a row if row_created == 1, or removing
 * the row if row_deleted == 1).
 *
 * AFTER calling this routine, the agent will delete the
 * undo_info.
 */
void icpTable_set_commit( netsnmp_request_group *rg )
{
    FILE *fp=NULL;
    stVarBindArgs stargs[ICP_NO_OF_FIELDS];
    char acstrTmpOID[ICP_MAX_VAR_LEN];
    int icnt;
    short srecCount = 0;
    short iIndex = 0;
    short sSet = 0;
    short sAction = 0;
    short tmpCount;    
    short sRowStatus = 0;
    short subcnt = 0; 
    netsnmp_variable_list *var;
    icpTable_context *ctx = NULL;
    icpTable_context *row_ctx = (icpTable_context *)rg->existing_row;
    icpTable_context *undo_ctx = (icpTable_context *)rg->undo_info;
    netsnmp_request_group_item *current;
        /*Load the Record in a File*/

   fp = fopen (ICP_CONTAINER_FILE, "wb");
   if (fp == (FILE *) NULL)
   {
       printf("icpTable_set_commit",
                    "Couldn't open CONTAINER_FILE\n");
        return;
   }//end of if
   ctx = CONTAINER_FIRST(cb.container);
   if (ctx)
   {
        /* Assign ctx index->oids to idx */
        ctx->idx = (unsigned long) *ctx->index.oids;

       if (fwrite (ctx, sizeof (icpTable_context), 1, fp) != 1)
       {
           printf("icpTable_set_commit",
                       "1 write ctx error\n");
           return;
       }//end of if (fwrite)
    }//end of if (ctx)

    while (ctx)
    {
        ctx = CONTAINER_NEXT(cb.container,ctx);
        if(ctx)
        {
            /* Assign ctx index->oids to idx */
            ctx->idx = (unsigned long) *ctx->index.oids;

            if (fwrite (ctx, sizeof (icpTable_context), 1, fp) != 1)
            {
               printf("icpTable_set_commit",
                           "2 write ctx error \n");
	       fclose(fp);
               return;
            }//end of if (fwrite)
        }//end of if (ctx)
    }//end of while (ctx)

    fclose (fp);

   if(row_ctx->icpStatus ==2){
	   printf("Inside -set commit rowstatus\n");
           return;	   
    }	
    /*
     * loop through columns
     */
    for( current = rg->list; current; current = current->next ) {

        var = current->ri->requestvb;
	memset(acstrTmpOID,'\0',ICP_MAX_VAR_LEN);
        memset(stargs[srecCount].acVariable,0,sizeof(stargs[srecCount].acVariable));
        for(icnt=0;icnt<var->name_length;icnt++)
        {
          sprintf(acstrTmpOID, ".%lu",var->name[icnt]);
          strcat (stargs[srecCount].acVariable,acstrTmpOID);
        }//for icnt
    printf("Variable: %s\n",stargs[srecCount].acVariable);
    
    sprintf(acstrTmpOID,"%lu",var->name[(var->name_length-1)]);
    printf("Value of tmpoid : %c\n",acstrTmpOID);
    iIndex = atoi(acstrTmpOID);
    printf("Value of index after conversion: %d\n",iIndex);
    
    switch ((short) var->type)
    {
       case ASN_INTEGER:
          stargs[srecCount].cType='i';
          printf("Type is integer : %d\n",(short) var->type);
          printf("Value: %d\n",*(var->val.integer));
          memset (stargs[srecCount].pcValue, '\0', sizeof (stargs[srecCount].pcValue));
          sprintf (stargs[srecCount].pcValue, "%ld",  *(var->val.integer));
          printf("****** After copying args[%d].pcValue=%s\n",
                    srecCount, stargs[srecCount].pcValue);
       break;

       case ASN_OCTET_STR:
          stargs[srecCount].cType='s';
          printf("Type is an OCTET string\n");
          printf("%d\n",(short) var->type);
          //printf("Value: %s\n",var->val.string);
          memset (stargs[srecCount].pcValue,'\0',sizeof (stargs[srecCount].pcValue));
          memcpy (stargs[srecCount].pcValue, var->val.string,var->val_len);
          printf("Value after copying: %s\n",stargs[srecCount].pcValue);
       break;

       default:
          printf ("WARNING: TYPE NOT HANDLED....\n");
       break;
    }//switch         
        

    switch(current->tri->colnum) {

        case COLUMN_ICPUSERID:
            /** DisplayString = ASN_OCTET_STR */
        break;

        case COLUMN_ICPCOMMAND:
	    sSet++;
            /** DisplayString = ASN_OCTET_STR */
        break;

        case COLUMN_ICPSTATUS:
            sRowStatus = atoi(stargs[srecCount].pcValue);
	    printf("RowStatus: %d\n",sRowStatus);
            /** RowStatus = ASN_INTEGER */
        break;

        default: /** We shouldn't get here */
            netsnmp_assert(0); /** why wasn't this caught in reserve1? */
        }//end of switch

    srecCount++; 

    }
    
    printf("Value of set: %d\n",sSet);
    

       /*Send Trap only when the mandatory fields are set*/
        switch (sRowStatus){
	   case ICP_ROW_CREATE:
               if (sSet == 1){
                   printf("Inside Sending Trap\n");
		   sAction = ICP_TRAP; 
               }//if
	    break;
	   case ICP_ROW_DELETE:
	      printf("Inside Row Delete\n");
	      sAction = ICP_TRAP; 
	      break;   
	    default:
	       printf("Inside Modify value of index: %d\n",iIndex);
               	tmpCount = srecCount; 
	        printf("srecCpount /Before calling search: %d\n",srecCount);	
	        if(icpTable_Search_Index_From_File (iIndex) == ICP_SUCCESS)
	        {
		       for(subcnt =0;subcnt<tmpCount;subcnt++)
		       {
			  printf("stargs[%d].value: %s\n",subcnt,stargs[subcnt].acVariable);
			  printf("sticpTrap.value: %s\n",sticpTrap_Var.acVariable);
			  if (strcmp(sticpTrap_Var.acVariable,stargs[subcnt].acVariable) == 0){
                            printf("Inside Compare\n");		  
			    break;
			  }//end of if
		       }//end of for subcnt
                       if(subcnt == tmpCount)
			{
			printf("Inside modify loop-srecCount: %d\n",srecCount);  
	                memset (stargs[srecCount].acVariable,'\0',ICP_MAX_OID_LEN);
                        memcpy (stargs[srecCount].acVariable,sticpTrap_Var.acVariable,
				       strlen(sticpTrap_Var.acVariable));
                        stargs[srecCount].cType =sticpTrap_Var.cType;
			    
		        memset (stargs[srecCount].pcValue,'\0',ICP_MAX_VAR_LEN);
                        memcpy (stargs[srecCount].pcValue,sticpTrap_Var.pcValue,
		      		       strlen(sticpTrap_Var.pcValue));
			srecCount++;
			}//end of if
                    sAction = ICP_TRAP;
	       }//end of check      
	       break;
	}//switch
	printf("SRecCount after modify: %d\n",srecCount);
        for(icnt =0;icnt<srecCount;icnt++){
	       printf("OID inside trap: %s\n",stargs[icnt].acVariable);
	       printf("OID Value : %s\n",stargs[icnt].pcValue);

        }	       
        printf("Action: %d\n",sAction);
	printf("RowStatus: %d\n",sRowStatus);
	
        if(sAction == ICP_TRAP){
           printf("Inside sAction\n");
          	   
           icnt=icpTable_send_snmptrap(srecCount,stargs,2504);
           printf("Code exited with %d\n",icnt);
	}

    /*
     * done with all the columns. Could check row related
     * requirements here.
     */
}

