/************************************************************
 *
 * Initialize the icpTable table by defining its contents and how it's structured
 */
void
initialize_table_icpTable(void)
{
    netsnmp_table_registration_info *table_info;

    if(my_handler) {
        snmp_log(LOG_ERR, "initialize_table_icpTable_handler called again\n");
        return;
    }

    memset(&cb, 0x00, sizeof(cb));

    /** create the table structure itself */
    table_info = SNMP_MALLOC_TYPEDEF(netsnmp_table_registration_info);

    /* if your table is read only, it's easiest to change the
       HANDLER_CAN_RWRITE definition below to HANDLER_CAN_RONLY */
    my_handler = netsnmp_create_handler_registration("icpTable",
                                             netsnmp_table_array_helper_handler,
                                             icpTable_oid,
                                             icpTable_oid_len,
                                             HANDLER_CAN_RWRITE);
            
    if (!my_handler || !table_info) {
        snmp_log(LOG_ERR, "malloc failed in "
                 "initialize_table_icpTable_handler\n");
        return; /** mallocs failed */
    }

    /***************************************************
     * Setting up the table's definition
     */
    /*
     * TODO: add any external indexes here.
     */

    /*
     * internal indexes
     */
        /** index: icpIndex */
        netsnmp_table_helper_add_index(table_info, ASN_UNSIGNED);

    table_info->min_column = icpTable_COL_MIN;
    table_info->max_column = icpTable_COL_MAX;

    /***************************************************
     * registering the table with the master agent
     */
    cb.get_value = icpTable_get_value;
    cb.container = netsnmp_container_find("icpTable_primary:"
                                          "icpTable:"
                                          "table_container");
#ifdef icpTable_IDX2
    netsnmp_container_add_index(cb.container,
                                netsnmp_container_find("icpTable_secondary:"
                                                       "icpTable:"
                                                       "table_container"));
    cb.container->next->compare = icpTable_cmp;
#endif
#ifdef icpTable_SET_HANDLING
    cb.can_set = 1;
#ifdef icpTable_ROW_CREATION
    cb.create_row = (UserRowMethod*)icpTable_create_row;
#endif
    cb.duplicate_row = (UserRowMethod*)icpTable_duplicate_row;
    cb.delete_row = (UserRowMethod*)icpTable_delete_row;
    cb.row_copy = (Netsnmp_User_Row_Operation *)icpTable_row_copy;

    cb.can_activate = (Netsnmp_User_Row_Action *)icpTable_can_activate;
    cb.can_deactivate = (Netsnmp_User_Row_Action *)icpTable_can_deactivate;
    cb.can_delete = (Netsnmp_User_Row_Action *)icpTable_can_delete;

    cb.set_reserve1 = icpTable_set_reserve1;
    cb.set_reserve2 = icpTable_set_reserve2;
    cb.set_action = icpTable_set_action;
    cb.set_commit = icpTable_set_commit;
    cb.set_free = icpTable_set_free;
    cb.set_undo = icpTable_set_undo;
#endif
    DEBUGMSGTL(("initialize_table_icpTable",
                "Registering table icpTable "
                "as a table array\n"));
    netsnmp_table_container_register(my_handler, table_info, &cb,
                                     cb.container, 1);
}

/************************************************************
 * icpTable_get_value
 */
int icpTable_get_value(
            netsnmp_request_info *request,
            netsnmp_index *item,
            netsnmp_table_request_info *table_info )
{
    netsnmp_variable_list *var = request->requestvb;
    icpTable_context *context = (icpTable_context *)item;

    switch(table_info->colnum) {

        case COLUMN_ICPINDEX:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->icpIndex,
                         sizeof(context->icpIndex) );
        break;
    
        case COLUMN_ICPUSERID:
            /** DisplayString = ASN_OCTET_STR */
            snmp_set_var_typed_value(var, ASN_OCTET_STR,
                         (char*)&context->icpUserId,
                         context->icpUserId_len );
        break;
    
        case COLUMN_ICPCOMMAND:
            /** DisplayString = ASN_OCTET_STR */
            snmp_set_var_typed_value(var, ASN_OCTET_STR,
                         (char*)&context->icpCommand,
                         context->icpCommand_len );
        break;
    
        case COLUMN_ICPSTATUS:
            /** RowStatus = ASN_INTEGER */
            snmp_set_var_typed_value(var, ASN_INTEGER,
                         (char*)&context->icpStatus,
                         sizeof(context->icpStatus) );
        break;
    
    default: /** We shouldn't get here */
        snmp_log(LOG_ERR, "unknown column in "
                 "icpTable_get_value\n");
        return SNMP_ERR_GENERR;
    }
    return SNMP_ERR_NOERROR;
}

/************************************************************
 * icpTable_get_by_idx
 */
const icpTable_context *
icpTable_get_by_idx(netsnmp_index * hdr)
{
    return (const icpTable_context *)
        CONTAINER_FIND(cb.container, hdr );
}

int icpTable_send_snmptrap (int length, stVarBindArgs *stTrapPDU, int port)
{
    netsnmp_session session, *ss;
    netsnmp_pdu       *pdu, *response;
    in_addr_t       *pdu_in_addr_t;
    oid            name[ICP_MAX_OID_LEN];
    size_t         name_length;
    int            arg;
    int            status;
    char       *trap = NULL, *specific = NULL, *description =
    NULL, *agent = NULL;
    char       *prognam;
    int            exitval = 0;
    int            icnt = 0;
    char**        argv;
    int            argc;
    struct in_addr  laddr;
    netsnmp_variable_list *tempPtr;
    char * value; 
    int intValue;
  
   printf("trapPDU var=%s type=%c val=%s\n", stTrapPDU[0].acVariable,
             stTrapPDU[0].cType, stTrapPDU[0].pcValue);

    /** initialize session to default values*/
     
    snmp_sess_init (&session);


    /* Initialize session values - DUMMY */
    session.version = SNMP_VERSION_2c;
    session.timeout = 1 * 1000000L;
    session.retries = 3;
    session.community = (unsigned char *) "public";
    session.community_len = strlen (session.community);

     
    session.peername = "localhost";

    /*DEBUGMSGTL(("send_snmptrap", "trapIP=%lu peername=%s trapPort=%ld\n",
                ctx->trapDestinationIP, session.peername, ctx->trapPort));*/

    SOCK_STARTUP;

    session.callback = icpTable_snmp_input;
    session.callback_magic = NULL;

    
    netsnmp_ds_set_int(NETSNMP_DS_LIBRARY_ID, NETSNMP_DS_LIB_DEFAULT_PORT,
               (int) port);

    if (session.version == SNMP_VERSION_3) {
    /*
     * for traps, we use ourselves as the authoritative engine
     * which is really stupid since command line apps don't have a
     * notion of a persistent engine.  Hence, our boots and time
     * values are probably always really wacked with respect to what
     * a manager would like to see.
     *
     * The following should be enough to:
     *
     * 1) prevent the library from doing discovery for engineid & time.
     * 2) use our engineid instead of the remote engineid for
     * authoritative & privacy related operations.
     * 3) The remote engine must be configured with users for our engineID.
     *
     * -- Wes
     */

    /*
     * setup the engineID based on IP addr.     Need a different
     * algorthim here.  This will cause problems with agents on the
     * same machine sending traps.
     */
    setup_engineID(NULL, NULL);

    /*
     * pick our own engineID
     */
    if (session.securityEngineIDLen == 0 ||
        session.securityEngineID == NULL) {
        session.securityEngineID =
        snmpv3_generate_engineID(&session.securityEngineIDLen);
    }
    if (session.contextEngineIDLen == 0 ||
        session.contextEngineID == NULL) {
        session.contextEngineID =
        snmpv3_generate_engineID(&session.contextEngineIDLen);
    }

    /*
     * set boots and time, which will cause problems if this
     * machine ever reboots and a remote trap receiver has cached our
     * boots and time...  I'll cause a not-in-time-window report to
     * be sent back to this machine.
     */
    if (session.engineBoots == 0)
        session.engineBoots = 1;
    if (session.engineTime == 0)    /* not really correct, */
        session.engineTime = get_uptime();    /* but it'll work. Sort of. */
    }

    ss = snmp_open(&session);
    if (ss == NULL) {
    /*
     * diagnose snmp_open errors with the input netsnmp_session pointer
     */
    snmp_sess_perror("snmptrap", &session);

    SOCK_CLEANUP;
    exit(1);
    }

    if (session.version == SNMP_VERSION_1) {
    pdu = snmp_pdu_create(SNMP_MSG_TRAP);
    pdu_in_addr_t = (in_addr_t *) pdu->agent_addr;

     if (++arg >= argc) {
        fprintf(stderr, "Missing agent parameter\n");
        /* usage(); - DUMMY */
        SOCK_CLEANUP;
        exit(1);
    }
    agent = argv[arg];
    if (agent != NULL && strlen(agent) != 0) {
      //  *pdu_in_addr_t = parse_address(agent);
    } else {
        *pdu_in_addr_t = get_myaddr();
    }
    if (++arg == argc) {
        fprintf(stderr, "Missing generic-trap parameter\n");
        /* usage(); - DUMMY */
        SOCK_CLEANUP;
        exit(1);
    }
    trap = argv[arg];
    pdu->trap_type = atoi(trap);
    if (++arg == argc) {
        fprintf(stderr, "Missing specific-trap parameter\n");
        /* usage(); - DUMMY */
        SOCK_CLEANUP;
        exit(1);
    }
    specific = argv[arg];
    pdu->specific_type = atoi(specific);
    if (++arg == argc) {
        fprintf(stderr, "Missing uptime parameter\n");
        /* usage() - DUMMY */;
        SOCK_CLEANUP;
        exit(1);
    }
    description = argv[arg];
    if (description == NULL || *description == 0)
        pdu->time = get_uptime();
    else
        pdu->time = atol(description);
    } else {
    long        sysuptime;
    char        csysuptime[20];

    pdu = snmp_pdu_create(SNMP_MSG_TRAP2);

    /* trap = argv[arg]; */ /* This is sysuptime */
    /* Always get the sysuptime from system, temporarily set *trap=0 */

    sysuptime = get_uptime();
    sprintf(csysuptime, "%ld", sysuptime);
    trap = csysuptime;

    /*snmp_add_var(pdu, objid_sysuptime,
             sizeof(objid_sysuptime) / sizeof(oid), 't', trap);*/

   /* if (snmp_add_var
        (pdu, objid_snmptrap, sizeof(objid_snmptrap) / sizeof(oid),
         'o', NURI_ENTERPRISE_OID) != 0) {
        
        printf ("***** objid_snmptrap problem...exiting....\n");
        SOCK_CLEANUP;
        exit(1);
    }*/
    }
    name_length = ICP_MAX_OID_LEN;
    for (icnt = 0; icnt < length; icnt++)
    {
	memset(name,'\0',ICP_MAX_OID_LEN);      
        if (!snmp_parse_oid(stTrapPDU[icnt].acVariable, name, &name_length)) {
           printf("OID: %s\n",stTrapPDU[icnt].acVariable);
           printf ("****** argument oid problem.................\n");
           SOCK_CLEANUP;
           exit(1);
        }
        if (snmp_add_var
        (pdu, name, name_length, stTrapPDU[icnt].cType, stTrapPDU[icnt].pcValue) != 0) {
           printf ("****** name=%s name_length=%d SNMPTRAP argument type and value problem..............\n", name, name_length);
           SOCK_CLEANUP;
           exit(1);
        }
    }//for
    status = snmp_send(ss, pdu) == 0;
    if (status) {
    snmp_sess_perror("snmptrap", ss);
    snmp_free_pdu(pdu);
    exitval = 1;
  }

    snmp_close(ss);
    SOCK_CLEANUP;
    printf("Finishing sndtrap\n");
    return exitval;
}

